import React, {type ReactNode} from 'react';
import BlogPostItem from '@theme-original/BlogPostItem';
import {useLocation} from '@docusaurus/router';
import GiscusComponent from '@site/src/components/GiscusComponent';
import type BlogPostItemType from '@theme/BlogPostItem';
import type {WrapperProps} from '@docusaurus/types';

type Props = WrapperProps<typeof BlogPostItemType>;

export default function BlogPostItemWrapper(props: Props): ReactNode {
  const location = useLocation();
  const isBlogPostPage = location.pathname.startsWith('/blog/') && !location.pathname.endsWith('/blog/');

  // Access metadata through props
  const metadata = (props as any)?.content?.metadata;
  const frontMatter = metadata?.frontMatter || {};
  const {enableComments} = frontMatter as {enableComments?: boolean};

  return (
    <>
      <BlogPostItem {...props} />
      {enableComments && isBlogPostPage && (
        <div style={{ marginTop: '2rem' }}>
          <GiscusComponent />
        </div>
      )}
    </>
  );
}

import React from 'react';
import Giscus from '@giscus/react';
import { useColorMode } from '@docusaurus/theme-common';

export default function GiscusComponent(): React.ReactElement {
  const { colorMode } = useColorMode();

  return (
    <div style={{ marginTop: '2rem' }}>
      <Giscus
        id="comments"
        repo="austenstone/portfolio"
        repoId="R_kgDONSlqPQ" // Replace with your actual repo ID from https://giscus.app/
        category="Blog Comments"
        categoryId="DIC_kwDONSlqPc4ClUhV" // Replace with your actual category ID from https://giscus.app/
        mapping="pathname"
        term="Welcome to giscus!"
        reactionsEnabled="1"
        emitMetadata="0"
        inputPosition="top"
        theme={colorMode === 'dark' ? 'dark' : 'light'}
        lang="en"
        loading="lazy"
      />
    </div>
  );
}

