type Props = WrapperProps<typeof BlogPostItemType>;

export default function BlogPostItemWrapper(props: Props): ReactNode {
  const location = useLocation();
  const isBlogPostPage = location.pathname.startsWith('/blog/') && !location.pathname.endsWith('/blog/');

  // Access metadata through props with multiple fallback approaches
  const content = (props as any)?.content;
  const metadata = content?.metadata || content?.frontMatter;
  const frontMatter = metadata?.frontMatter || metadata || {};
  const {enableComments} = frontMatter as {enableComments?: boolean};

  // Debug logging
  console.log('BlogPostItem Debug:', {
    location: location.pathname,
    isBlogPostPage,
    enableComments,
    propsKeys: Object.keys(props),
    content: content ? Object.keys(content) : 'undefined',
    metadata: metadata ? Object.keys(metadata) : 'undefined',
    frontMatter
  });

  return (
    <>
      <BlogPostItem {...props} />
      {enableComments && isBlogPostPage && (
        <div style={{ marginTop: '2rem' }}>
          <h2>Comments</h2>
          <GiscusComponent />
        </div>
      )}
    </>
  );
}

