    enableComments = (props as any).content.metadata.frontMatter.enableComments;
  }

  // Approach 3: Check the actual props structure
  const propsStr = JSON.stringify(props, null, 2);
  console.log('Full props structure:', propsStr);

  return (
    <>
      <BlogPostItem {...props} />
      {enableComments && isBlogPostPage && (
        <div style={{ marginTop: '2rem' }}>
          <h2>Comments</h2>
          <GiscusComponent />
        </div>
      )}
    </>
  );
}

